mapper.select:
SELECT * FROM (
	SELECT ROWNUM rnum, th.* FROM (
		SELECT bbsNum, subject, content, hitCount, 
			TO_CHAR(reg_date, 'YYYY-MM-DD') reg_date, nickName
		FROM bbs b
		JOIN member m ON b.userId = m.userId
		JOIN member_detail d ON m.userId = d.userId
		
			
				
			
		
		ORDER BY bbsNum DESC
	) th WHERE ROWNUM <= #{end}
) WHERE rnum >= #{start}
