mapper.select:
	SELECT * FROM (        
    	SELECT ROWNUM rnum, tb.* FROM (	
			SELECT s.studyNum, memberNum, role, userId, studyName, studyGoal, studystatus
			FROM study s
			JOIN study_member sm ON s.studyNum = sm.studyNum
			WHERE userId = #{userId}
	    ) tb WHERE ROWNUM <= #{end}    
	) where rnum >= #{start}
